def moving_average(data, window_size):
    smoothed_data = []
    for i in range(len(data)):
        if i < window_size:
            smoothed_data.append(data[i])
        else:
            smoothed_value = sum(data[i - window_size:i]) / window_size
            smoothed_data.append(smoothed_value)
    return smoothed_data

